,p_template=>wwv_flow_imp.id(87464950895660666)
,p_display_sequence=>10
,p_region_template_options=>'#DEFAULT#:t-Region--scrollBody'
,p_component_template_options=>'#DEFAULT#:t-Report--altRowsDefault:t-Report--rowHighlight'
,p_source_type=>'NATIVE_SQL_REPORT'
,p_query_type=>'SQL'
,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT DISTINCT',
'    z.imie || '' '' || z.nazwisko AS Zawodnik,',
'    z.data_urodzenia AS "Data urodzenia",',
unistr('    z.narodowosc AS Narodowo\015B\0107,'),
'    ''Podpisano: '' || TO_CHAR(k.data_podpisania, ''YYYY-MM-DD'') || ',
unistr('    '', Zako\0144czono: '' || COALESCE(TO_CHAR(k.data_zakonczenia, ''YYYY-MM-DD''), ''N/A'') || '),
unistr('    '', Spodziewane zako\0144czenie: '' || TO_CHAR(k.data_spodziewanego_zakonczenia, ''YYYY-MM-DD'') AS "Dane kontraktu",'),
'    l.nazwa AS "Liga",',
unistr('    COALESCE(ROUND(ZAWODNIK_SREDNIA_LIGOWA_NA_SEZON(:P33_ID_WYBRANEGO_SEZONU, lws.id_ligi, z.id_zawodnika), 2), 0) AS "\015Arednia",'),
'    z.id_zawodnika',
'FROM ',
'    zawodnicy z',
'JOIN ',
'    kontrakty k ON z.id_zawodnika = k.id_zawodnika',
'JOIN ',
'    zespoly zes ON k.id_zespolu = zes.id_zespolu',
'JOIN ',
'    ligi_w_sezonach lws ON lws.id_ligi_w_sezonie IN (',
'        SELECT DISTINCT m.id_ligi_w_sezonie',
'        FROM ',
'            mecze m',
'        JOIN ',
'            ligi_w_sezonach lws ON lws.id_ligi_w_sezonie = m.id_ligi_w_sezonie',
'        JOIN ',
'            sezony s ON s.id_sezonu = lws.id_sezonu',
'        WHERE ',
'            (m.id_zesp_gosc = :P33_WYBRANY_ZESPOL OR m.id_zesp_gospodarz = :P33_WYBRANY_ZESPOL)',
'            AND s.rok = :P33_WYBRANY_SEZON',
'    )',
'JOIN ',
'    ligi l ON lws.id_ligi = l.id_ligi',
'WHERE ',
'    k.id_zespolu = :P33_WYBRANY_ZESPOL',
'    AND (',
'        k.data_podpisania <= TO_DATE(:P33_WYBRANY_SEZON || ''-12-31'', ''YYYY-MM-DD'') ',
'        AND COALESCE(k.data_zakonczenia, k.data_spodziewanego_zakonczenia) >= TO_DATE(:P33_WYBRANY_SEZON || ''-01-01'', ''YYYY-MM-DD'')',
'    )',
'ORDER BY ',
unistr('    "\015Arednia" DESC, "Liga";'),
''))
